import React,{useState,useEffect} from 'react';
import UserNavBar from '../../layout/UserNavbar';
import './UserPage.css';

import axios from 'axios';

function SubscriptionPage() {
    
const [userData,setUser]=useState({
        userr:{},
        userRefresh:true
        })

        const [subs,setSubs]=useState({
            subscriptions:'',
            errors:'',
            loading:false,
            offerList:[],
            refresh:0,
            index:0,
            })

    useEffect(() => {

console.log('token 1' )
const tok=localStorage.getItem("token");
console.log('token 2',tok )

//get user data to show it in subscription page
if(tok){
    console.log('token 3' )
    axios.get(`http://localhost:5000/api/verify?token=${tok}`)
    .then(res=>{
        if(res.data.success===true){
            console.log('verifié',tok);                     
            axios.get(`http://localhost:5000/api/getUser?token=${tok}`)
            .then(res=>{
                if(res.data.success===true){
                    console.log('user loaded',res.data.user);
                    setUser({userr:res.data.user})
                    console.log("userid",res.data.user._id);
                }else{
                    setUser({userRefresh:!userData.userRefresh})
                }
            })
        }
        else{
            setUser({userRefresh:!userData.userRefresh})
        }
    })
}
    }, [userData.userRefresh])


    useEffect(() => {
        if(userData.userr){
            console.log('subss',userData.userr);
            axios.get(`http://localhost:5000/api/getSubscriptions?userid=${userData.userr._id}`)
            .then(res=>{
                if(res.data.success===true){
                    console.log('gagaga')
                    console.log(res.data.subs)
                    setSubs({subscriptions:[...res.data.subs]})
                    console.log("hfhf",subs.subscriptions)
                    if(res.data.subs){
                    console.log("helooo")
                    let newArray=[];
                   
                    res.data.subs.forEach(element => {
                    console.log(element.OfferId)
                    axios.get(`http://localhost:5000/api/getOfferById?offerid=${element.OfferId}`)
                    .then(res=>{
                        if(res.data.success===true){
                                console.log(res.data.offers)
                
                                let off={calls:res.data.offers.calls,
                                internet:res.data.offers.internet,
                                duration:res.data.offers.duration,
                                price:res.data.offers.price,
                                description:res.data.offers.description
                            }
                            console.log('offer',off)                        
                            newArray.push(off)
                            setSubs({offerList:[...subs.offerList || [],off]})
                            //setSubs({offerList:[...subs.offerList || [],newArray[subs.index]]})
                            setSubs({index:subs.index+1})
                            console.log('index',subs.index)
                            console.log('offerlist foreach',subs.offerList)
                        
                        
                        }
                            else{
                                setSubs({errors:res.data.message})
                                setSubs({refresh:subs.refresh+1})
                            }
                    })
                    })
                   // setSubs({offerList:[...newArray]})
                    setSubs({loading:true})
                    console.log("newAray:",newArray);
                    console.log("offres:",subs.offerList);
                    console.log("loading:",subs.loading);
                    
                    }
                   
                }else {
            setSubs({refresh:subs.refresh+1})
                }
            })
        }else{
            setSubs({refresh:subs.refresh+1})
        }
    }, [subs.refresh])

    return (
        <>
        <UserNavBar />
        <h1 className="userTitle" style={{color:"#555"}}>Welcome to your space <br/>{!userData.userr ?  null : userData.userr.firstName} { !userData.userr ?  null : userData.userr.lastName} !</h1>
        <div className="UserSpace">
            <h2 >Your subscriptions</h2>
            <div className="cards" style={{top:"40px"}}>
            {

                !subs.loading ? <h2>Loading ...</h2> : 
                
                /*subs.errors ? <h2>{subs.errors}</h2> : */
                typeof  subs.offerList !== 'undefined' && subs.offerList.map((offer, i) =>{
                        return (
                            <h1>hahaa</h1>
                        /*<div className="card card-0" style={{width:"80%", height:"100px"}}>
                        <div class="card__icon"> ggg</div>
                        <h3 class="card__title">{offer.internet}+ {offer.calls}</h3>
    </div>*/
                        )}
                        )
                        }

            </div>
        </div>
        </>
    )
}

export default SubscriptionPage
