import React, { Component,useState } from 'react';
import './Register.css';
import {Link} from 'react-router-dom';
import axios from "axios"
import { useHistory } from "react-router-dom";
import {getFromStorage,setInStorage} from '../utils/storage';
const localStorageObjectName = "login_system_storage";



class Login extends Component {
  constructor(props) {
    super(props);
  this.State={
    email:"",
    password:"",
    token:''
};
  }

  componentDidMount() {
    // get the localstorage object
    /*const obj = getFromStorage(localStorageObjectName);
    if (obj && obj.token) {
      // get token from local storage
      const { token } = obj;
      console.log("token from local storage= ", token);*/
      const token=window.sessionStorage.getItem("token");
      // verify token
      axios.get(`/api/verify?token=${token}`)
        .then(res =>{
          if(res.data.success===true){
          
            this.setState({token:token})
            console.log('tok==',token)
          }
           
        } );
  }



render(){

  //const history =this.History();
 
  const handleChange = e => {
    e.preventDefault();
      const { name, value } = e.target
      this.setState({
        [name]: value
      });
      console.log("hh",this.state);
      
  }
 
 const login = (event)=>  {
    event.preventDefault();
    let user={email:'',password:''};

    console.log("ooooo",user.email, user.password);
    console.log("oohh",this.state.email);
    console.log("oohh",this.state.password);

       user={email:this.state.email,password:this.state.password};

      console.log("ooooo",user.email, user.password);
      if( user.email && user.password ){
        console.log("logiin");
          axios.post("http://localhost:5000/api/login", user)
          .then(res => {
            if(res.data.success===true){
              if(user.email==="amal@gmail.com"){
               // history.push('./AdminPage');
              }else{
             // history.push('./HomePage');
             window.sessionStorage.setItem("token", this.token);
             console.log("too",this.token);
            }}
           
     
        })
          console.log(' logged in');
      } else {
          alert("invlid input")
      }
      
  }
  
    return (
        <form className="form">
      <Link to="/" className="btn-flat">
        <i className="material-icons left">keyboard_backspace</i> Back to
        home
      </Link>
        <ul className="tab-group">
          <li className="tab"><a href="/register">Sign Up</a></li>
          <li className="tab active"><a>Log In</a></li>
        </ul>
        
        <div className="tab-content">
            <h1>Welcome Back!</h1>
            
            <form >
            
              <div className="field-wrap" noValidate >
              <input type="email" 
               placeholder="Adresse Email"
               required autoComplete="on"
               value={this.email}
               onChange={handleChange }
               name="email"
                            
               />


            </div>
            
            <div className="field-wrap" noValidate >
              <input type="password" 
              placeholder="Mot de passe" 
              required autoComplete="off"
              name="password"
              value={this.password}
              onChange={handleChange }
             />

            </div>
            
            <p className="forgot"><a href="#">Forgot Password?</a></p>
            
            <button className="button button-block"   onClick={login}>Log In</button>
            <p className="have-account"><a href="#">Vous n'avez pas de compte? Cr√©ez un!</a></p>
            </form>
  
          </div>
        
  </form> 
    )
}
}

export default Login
