import React,{ useState, useEffect } from 'react';
import '../pages/admin/AdminPage.css'
import axios from "axios";

function OffersDescription () {

const [ AdminData, setAdminData] = useState({     
  loaded: false,
  data:{},
  internet:'',
  calls:'',
  description:'',
  offers_loaded:false,
  offers_refresh:false,
  addOffer:false

});

const [refreshKey, setRefreshKey] = useState(0);
/*const [addOfferButton, handleAddOfferButton] = useState(false);*/
const handleAddOfferButton=()=>{
    setAdminData({ addOffer: !AdminData.addOffer});
}

/*const AddOffer=(intn,call,desc)=>{

}*/
     
const handleChange = e => {
  e.preventDefault();
    const { name, value } = e.target
    setAdminData({
        ...AdminData,
        [name]: value
    });
    
}
useEffect(() => {
console.log('kkk',refreshKey)
axios.get("http://localhost:5000/api/getAllOffers")
      .then(res => {
          if(res.data.success===true){
            console.log('new offerrr effect')
          setAdminData({ offers_loaded: true,data:res.data.data,offers_refresh:true });
          
          }else {
          setAdminData({ offers_loaded: false,data:"Error in loading data" });
          }
      })
}, [refreshKey]);


/*  useEffect(() => {
    console.log('hhff')
    setAdminData({ addOffer: true});
  
    }, [addOfferButton]);*/
       
   const addNewOffer = () => {
      console.log('okk',AdminData.calls);
      let offer={internet:AdminData.internet,calls:AdminData.calls,description:AdminData.description}
      axios.post(`http://localhost:5000/api/AddOffer`,offer)
      .then(res => {
          if(res.data.success===true){
           
            /*axios.get("http://localhost:5000/api/getAllOffers")
            .then(res => {
                if(res.data.success===true){*/
            console.log('new offerrr effect addoffer')

                setAdminData({ offers_loaded: true,data:res.data.data,offers_refresh: true});
                setRefreshKey(refreshKey +1)
                console.log('zzz','',AdminData.data);
                /*}else {
                setAdminData({ loaded: false,data:"Error in loading data" });
                }
            })
            console.log('deleted');*/
          
          }else {
          console.log('error in adding offer')
          }
      })
    }

     
  return (
      <>      
      <div class="input-content" id='Offers'>
            <h1>Offers</h1>
            {!AdminData.offers_refresh && !AdminData.offers_loaded  ? <p className='error'>Loading</p> : 

            <div className="offersContainer">
            <table class="table table-bordered">
            <thead>

            <tr >
                <th class="bg-danger" ></th>
                <th  class="bg-danger" >INTERNET</th>
                <th class="bg-danger" >CALLS</th>
                <th class="bg-danger" >DESCRIPTION</th>
               
            </tr>
            </thead>
            <tbody>
            {    AdminData.data.map((offer, i) =>
            <tr>

                <th >{i+1}</th>
                <td>{offer.internet}</td>
                <td>{offer.calls}</td>
                <td>{offer.description}</td>
                <td>ok</td>
            </tr>
            )}
            </tbody>
            </table>
            <button id='btn_show_add' class="btn btn-info" onClick={handleAddOfferButton}>+</button>
            </div>}
            { !AdminData.addOffer ? null : 

    <div class="input-content-wrap">
      <dl class="inputbox">
        <dt class="inputbox-title">Internet</dt>
        <dd class="inputbox-content">
          <input id="input0" type="text" required name="internet" value={AdminData.internet}/>
          <label for="input0">Internet</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <dl class="inputbox">
        <dt class="inputbox-title">Calls</dt>
        <dd class="inputbox-content">
          <input id="input1" type="text" required name="calls" value={AdminData.calls}  onChange={handleChange }/>
          <label for="input1">Calls</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <dl class="inputbox">
        <dt class="inputbox-title">Description</dt>
        <dd class="inputbox-content">
          <input id="input2" type="text" required name="description" value={AdminData.descrription}  onChange={handleChange }/>
          <label for="input2">Description</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <div class="btns">
          <button id='btn_add' class="btn btn-info" onClick={addNewOffer}  onChange={handleChange }>Add new offer</button>
      </div>
    </div>
            }
            
</div>
      </>
    )
}

export default OffersDescription
