import React,{ useState, useEffect } from 'react';
import './AdminPage.css';
import AdminNavBar from '../../layout/AdminNavbar';
import UserDescription from '../../layout/UserDescription';
import axios from "axios";



function  AdminPage () {
    
  const [ AdminData, setAdminData] = useState({     
    loaded: false,
    data:{},
    length:0,
    offers_loaded:false,

});

/*const SendIndex=({id})=>{
  let row=[...AdminData.data]
  row.splite(id,1)
  setAdminData({data:row})

}*/
       
useEffect(() => {
 
  // <div>{count}</div>
  axios.get("http://localhost:5000/api/getAllUsers")
        .then(res => {
            if(res.data.success===true){
            setAdminData({ loaded: true,data:res.data.data,length:res.data.length });
            }else {
            setAdminData({ loaded: false,data:"Error in loading data" });
            }
        })

}, []);
         

        // Trigger update
      
       
       /*deleteRow(id){
        console.log('aaaa',id)
        let row=[...this.state.data]
        console.log('aaaa',row)
        row.splice(id, 1)
        this.setState({ data:row });

      }*/
      const Delete = (event,id) => {
        
        console.log('okk');
        axios.post(`http://localhost:5000/api/DeleteUser?email=${user.email}`)
        .then(res => {
            if(res.data.success===true){
              event.preventDefault();
              let row=[...AdminData.data]
              row.splite(id,1)
              setAdminData({data:row})
              console.log('deleted');
            
            }else {
            console.log('error in deleting user')
            }
        })
      }

       
    return (
        <>

        <AdminNavBar />
       

        <div className="HomePageAdmin">
            <h1 className="titleADmin">
            ADMIN PANEL
            </h1>

            <div className="grid-container">
                <div className="grid-item" id='item1'>Users</div>
                <div className="grid-item" id='item2'>Offers</div>
                <div className="grid-item" id='item3' >Invoices</div>
                <div className="grid-item" id='item4'>subscription</div>
           
            </div>
        </div>


        
        <div className='UsersSection'>
            <div className="UsersConainer">
                <h1 className='UsersSectionTitle'>USERS LIST</h1>
                { !AdminData.loaded ? <p className='error'>Error</p> :                
                <table id="customers" >
                   <thead>
                <tr>
                    <th>ID</th>
                    <th>EMAIL</th>
                    <th>FIRST NAME</th>
                    <th>LAST NAME</th>
                </tr>  
                 </thead>  
             
                 <tbody>                  
                        {    AdminData.data.map((user, i) =>
                            <tr >
                                <td>{user.id}</td>
                                <td>{user.email}</td>
                                <td>{user.firstname}</td>
                                <td>{user.lastname}</td>
                                <td><button type="button" onClick={(event)=>Delete(event,i)} className="btn btn-danger">Delete</button>
                                </td>
                            </tr>)
                        }
                   </tbody>  
                </table>
                }

                    

            </div>
        </div>

        <div class="input-content">
            <h1>Offers</h1>

    <div class="input-content-wrap">
      <dl class="inputbox">
        <dt class="inputbox-title">Internet</dt>
        <dd class="inputbox-content">
          <input id="input0" type="text" required/>
          <label for="input0">ID</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <dl class="inputbox">
        <dt class="inputbox-title">Calls</dt>
        <dd class="inputbox-content">
          <input id="input1" type="text" required/>
          <label for="input1">Calls</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <dl class="inputbox">
        <dt class="inputbox-title">Description</dt>
        <dd class="inputbox-content">
          <input id="input2" type="text" required/>
          <label for="input2">Description</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <div class="btns">
          <button id='btn_add' class="btn btn-info">Add new offer</button>
      </div>
    </div>

</div>


       
        </>
    )
}

export default AdminPage
