import React, { Component,useState } from 'react';
import './Register.css';
import {Link} from 'react-router-dom';
import axios from "axios"
import { useHistory } from "react-router-dom";
import {getFromStorage,setInStorage} from '../utils/storage';
import { withRouter } from "react-router-dom";
const localStorageObjectName = "login_system_storage";




class Login extends Component {
  constructor(props) {
    super(props);
    this.State={
      email:"",
      password:"",
      token:'',
      user:{}
};

  }

  componentDidMount({setLoginToken,setLoginUser}) {
    
      const tokenn=window.sessionStorage.getItem("token");
      console.log('t1',tokenn);
      // verify token
      if(tokenn!== null){
        console.log('t2',tokenn);
      axios.get(`http://localhost:5000/api/verify?token=${tokenn}`)
        .then(res =>{
          if(res.data.success===true){
          console.log('verifié',tokenn);
          this.setState({token:tokenn});
          //send token to app
          setLoginToken(res.data.token)
      axios.get(`http://localhost:5000/api/getUser?token=${tokenn}`)
      .then(res=>{
        console.log('okkk',res.data.user);
        let userFound=res.data.user;
        this.setState({user:userFound})
        setLoginUser(res.data.user)
        console.log(res.data.user);
      })

      console.log('token==',this.state.token)
          }
        });
      }
    }


render(){
  const handleChange = e => {
    e.preventDefault();
      const { name, value } = e.target
      this.setState({
        [name]: value
      });
      console.log("hh",this.State);
  }
 
 const login = (event)=>  {
    event.preventDefault();
    let user={email:'',password:''};
    user={email:this.state.email,password:this.state.password};

    console.log("ooooo",user.email, user.password);
    if( user.email && user.password ){
        console.log("logiin");
          axios.post("http://localhost:5000/api/login", user)
          .then(res => {
            console.log('res.data.token',res.data.token);
            window.sessionStorage.setItem("token", res.data.token);
            this.setState({token:res.data.token});
            console.log(this.state,"tuuuo",this.state.token);

            if(res.data.success===true){

              if(user.email==="amal@gmail.com"){
                  this.props.history.push("/AdminPage");

              }else{
                  
                  window.sessionStorage.setItem("token", this.state.token);
                  this.props.history.push("/HomePage");
                  console.log("too",this.state.token);
            }}
        })
          console.log(' logged in');
      } else {
          alert("invlid input")
      }
      
  }
  
    return (
      <form className="form">
      <Link to="/" className="btn-flat">
        <i className="material-icons left">keyboard_backspace</i> Back to
        home
      </Link>
        <ul className="tab-group">
          <li className="tab"><a href="/register">Sign Up</a></li>
          <li className="tab active"><a>Log In</a></li>
        </ul>
        
        <div className="tab-content">
            <h1>Welcome Back!</h1>
            
            <form >
            
              <div className="field-wrap" noValidate >
              <input type="email" 
               placeholder="Adresse Email"
               required autoComplete="on"
               value={this.email}
               onChange={handleChange }
               name="email"
                            
               />


            </div>
            
            <div className="field-wrap" noValidate >
              <input type="password" 
              placeholder="Mot de passe" 
              required autoComplete="off"
              name="password"
              value={this.password}
              onChange={handleChange }
             />

            </div>
            
            <p className="forgot"><a href="#">Forgot Password?</a></p>
            
            <button className="button button-block"   onClick={login}>Log In</button>
            <p className="have-account"><a href="#">Vous n'avez pas de compte? Créez un!</a></p>
            </form>
  
          </div>
        
  </form> 
    )
}

}

export default withRouter (Login);
