import React ,{Component,useState} from 'react';
import './Register.css';
import {Link} from 'react-router-dom';
import {getFromStorage,setInStorage} from '../utils/storage';
import 'whatwg-fetch';
import axios from "axios"
import { useHistory } from "react-router-dom";

const localStorageObjectName = "login_system_storage";

function Register ()  {


  const history = useHistory()
  const [ user, setUser] = useState({
      firstName: "",
      lastName: "",
      email:"",
      password:"",
      password2: "",
      token:''
      
  })

  const componentDidMount= ()=> {
    // get the localstorage object
    const obj = getFromStorage(localStorageObjectName);
    if (obj && obj.token) {
      // get token from local storage
      const { token } = obj;

      // verify token
      axios.get(`/api/verify?token=${token}`)
        .then(res =>{
          if(res.data.success==='true'){
            this.setState({token:token})
          }
           
        } );
  }}


  const handleChange = e => {
    e.preventDefault();
      const { name, value } = e.target
      setUser({
          ...user,
          [name]: value
      });
      
  }

  const register = (event) => {
    event.preventDefault();
      const { firstName,lastName, email, password, password2,token } = user
      console.log("****",user);
      if( firstName && lastName && email && password && (password === password2)){
        console.log("okkkkkkkkkkkkkkkkkkkkkk");
          axios.post("http://localhost:5000/api/register", user)
          .then( res => {
            if(res.data.success===true){
              setInStorage(localStorageObjectName, {  token });
              console.log(token,"toooooken");

              history.push("/HomePage")}
          })
          console.log('c inserÃ©');

      } else {
          alert("invlid input")
      }
      
  }
   
    return (
      <>
      {console.log(user)}
        <div className="form" >
        <Link to="/" className="btn-flat">
        <i className="material-icons left">keyboard_backspace</i> Back to
        home
      </Link>
        <ul className="tab-group">
          <li className="tab active"><a>Sign Up</a></li>
          <li className="tab"><a href="/login">Log In</a></li>
        </ul>
        
        <div className="tab-content">
         
            <h1>CrÃ©er un compte</h1>
            
            <form>
            
            
              <div className="field-wrap2" noValidate >
                <input 
                placeholder="PrÃ©nom" 
                name="firstName"
                type="text" 
                required 
                autoComplete="on"
                value={user.firstName}
                onChange={ handleChange }
                />
              </div>

              <div className="field-wrap2" noValidate >
                <input 
                placeholder="Nom" 
                name="lastName"
                type="text" 
                required 
                autoComplete="on"
                value={user.lastName}
                onChange={handleChange }
                 />
              </div>
              
  
            <div className="field-wrap" noValidate >
              <input 
              placeholder="Adresse mail" 
              type="email" required 
              name="email"
              autoComplete="on"
              value={user.email}
              onChange={handleChange }
              />
            </div>
            

            <div className="field-wrap" noValidate >
              <input 
              placeholder="Mot de passe" 
              type="password" required 
              autoComplete="off"
              name="password"
              value={user.password}
              onChange={handleChange }
              />
           
            </div>
            
            <div className="field-wrap" noValidate >
              <input 
              placeholder="Confirmez Mot de passe" 
              type="password"required 
              autoComplete="off"
              name="password2"
              /*onChange={e=>this.password2=e.target.value}*/
              value={user.password2}
              onChange={handleChange }
              />
            </div>
            

            <button method="post"  onClick={register}  className="button button-block">Valider</button>
            <p className="have-account"><a href="#">Vous avez un compte? connectez vous!</a></p>
            </form>

          
        </div>
        
  </div> 
  </>
    );

}

export default Register
