import React,{useState,useEffect} from 'react';
import UserNavBar from '../../layout/UserNavbar';
import './UserPage.css';

import axios from 'axios';

function SubscriptionPage() {

    const [userr,getUser]=useState()
    const [subs,setSubs]=useState()

    useEffect(() => {
     console.log('t2',2222);
   
       const tokenn=localStorage.getItem("token");
       //get user data to show it n user page
       if(tokenn!== null){
         console.log('t2',tokenn);
          axios.get(`http://localhost:5000/api/verify?token=${tokenn}`)
           .then(res =>{
             if(res.data.success===true){
             console.log('verifié',tokenn);                     
             axios.get(`http://localhost:5000/api/getUser?token=${tokenn}`)
             .then(res=>{
               console.log('okkk',res.data.user);
               getUser(res.data.user)
               //setLoginUser(res.data.user)
               console.log(res.data.user);
                axios.get(`http://localhost:5000/api/getSubscriptions?userid=${userr._id}`)
                .then(res=>{
                    console.log('okkk',res.data.subs);
                    setSubs(res.data.subs)
             })     
             })
            }
        })
        }
       }, [])


    return (
        <>
        <UserNavBar />
        <h1 className="userTitle" style={{color:"#555"}}>Welcome to your space <br/>{!userr ?  null : userr.firstName} { !userr ?  null : userr.lastName} !</h1>
        <div className="UserSpace">
            <h2 >Your subscriptions</h2>
            <div className="cards" style={{top:"40px"}}>
            <div className="card card-0" style={{width:"80%"}}>
            <div class="card__icon"></div>
            <h3 class="card__title"> </h3>
            </div>
            </div>
        </div>
        </>
    )
}

export default SubscriptionPage
