import React,{useState,useEffect} from 'react';
import UserNavBar from '../../layout/UserNavbar';
import './UserPage.css';

import axios from 'axios';

function SubscriptionPage() {
    
const [subs,setSubs]=useState({
        userr:{},
        subscriptions:'',
        errors:'',
        loading:false,
        offerList:[],
        refresh:0,
        index:0,
        userRefresh:true,
        userLoading:false
    })

    useEffect(() => {

console.log('token 1' )
const tok=localStorage.getItem("token");
console.log('token 2',tok )

//get user data to show it in subscription page
if(tok){
    console.log('token 3' )
    axios.get(`http://localhost:5000/api/verify?token=${tok}`)
    .then(res=>{
        if(res.data.success===true){
            console.log('verifié',tok);                     
            axios.get(`http://localhost:5000/api/getUser?token=${tok}`)
            .then(res=>{
                if(res.data.success===true){
                    console.log('user loaded',res.data.user);
                    setSubs({userr:res.data.user})

                    console.log("userid",res.data.user._id);
                    if(subs.userr){
                    setSubs({userLoading:true})
                        
                    }
                }else{
                    setSubs({userRefresh:!subs.userRefresh})
                }
            })
        }
        else{
            setSubs({userRefresh:!subs.userRefresh})
        }
    })

}
    }, [subs.userRefresh])

    useEffect(() => {

    }, [subs.refresh,subs.index])

    return (
        <>
        <UserNavBar />
        <h1 className="userTitle" style={{color:"#555"}}>Welcome to your space <br/>{!subs.userr ?  null : subs.userr.firstName} { !subs.userr ?  null : subs.userr.lastName} !</h1>
        <div className="UserSpace">
            <h2 >Your subscriptions</h2>
            <div className="cards" style={{top:"40px"}}>
            {

                !subs.loading ? <h2>Loading ...</h2> : 
                
                /*subs.errors ? <h2>{subs.errors}</h2> : */
                typeof  subs.offerList !== 'undefined' && subs.offerList.map((offer, i) =>{
                        return (
                            <h1>hahaa</h1>
                        /*<div className="card card-0" style={{width:"80%", height:"100px"}}>
                        <div class="card__icon"> ggg</div>
                        <h3 class="card__title">{offer.internet}+ {offer.calls}</h3>
    </div>*/
                        )}
                        )
                        }

            </div>
        </div>
        </>
    )
}

export default SubscriptionPage
