import React ,{useEffect,useState}from 'react';
import '../pages/user/UserPage.css';
import axios from 'axios';

function UserHomeSection() {

const [userr,getUser]=useState()

 useEffect(async () => {
  console.log('t2',2222);

    const tokenn=localStorage.getItem("token");
    //get user data to show it n user page
    if(tokenn!== null){
      console.log('t2',tokenn);
      await axios.get(`http://localhost:5000/api/verify?token=${tokenn}`)
        .then(res =>{
          if(res.data.success===true){
          console.log('verifié',tokenn);                     
          axios.get(`http://localhost:5000/api/getUser?token=${tokenn}`)
          .then(res=>{
            console.log('okkk',res.data.user);
            getUser(res.data.user)
            //setLoginUser(res.data.user)
            console.log(res.data.user);
          })     
          }
        });
      }

    }, [userr])

    return (
        <>
            <div className="userSection">
                <h1 className="userTitle">Hello !</h1>
                <h1 className="userTitle">{userr.firstName} {userr.lastName}</h1>
                
            </div>
        </>
    )
}

export default UserHomeSection
