import React from 'react';
import '../pages/user/UserPage.css';

function UserOffers() {
  const [UserOffer,setUserOffer]=useState({
    offers_loaded:false,
    data:{}
  })
  useEffect(() => {

    axios.get("http://localhost:5000/api/getAllOffers")
          .then(res => {
              if(res.data.success===true){
              setAdminData({ offers_loaded: true,data_offers:res.data.data });
              console.log('offers loaded ');
              }else {
              setAdminData({ offers_loaded: false,data_offers:"Error in loading data" });
              }
          })
    }, []);

    return (
        <>

        <div class="cards">

{!offers_loaded ? null :
    <div class="card card-1">
      <div class="card__icon"><i class="fas fa-bolt"></i></div>
      <h2 class="card__title">Lorem ipsum dolor sit amet, consectetur adipiscing elit.</h2>
      <p class="card__apply">
        <a class="card__link" href="#">Subscribe <i class="fas fa-arrow-right"></i></a>
      </p>
    </div>
  }
   
  </div>
            
        </>
    )
}

export default UserOffers
