import React,{useState,useEffect} from 'react';
import UserNavBar from '../../layout/UserNavbar';
import './UserPage.css';

import axios from 'axios';

function SubscriptionPage() {
    
    const [subs,setSubs]=useState({
        userr:{},
        subscriptions:'',
        errors:'',
        loading:false,
        subList:[]
    })
    
    

    useEffect(() => {
        async function fetchMyAPI() {
     console.log('t2',2222);
   
       const tokenn=localStorage.getItem("token");
       //get user data to show it n user page
       if(tokenn!== null){
            console.log('t2',tokenn);
            axios.get(`http://localhost:5000/api/verify?token=${tokenn}`)
                .then(res =>{
                if(res.data.success===true){
                    console.log('verifié',tokenn);                     
                    axios.get(`http://localhost:5000/api/getUser?token=${tokenn}`)
                    .then(res=>{
                        console.log('okkk',res.data.user);
                        setSubs({userr:res.data.user})
                        console.log(res.data.user._id);
                        if(subs.userr){
                            console.log('subss',subs.userr);

                        axios.get(`http://localhost:5000/api/getSubscriptions?userid=${subs.userr._id}`)
                        .then(res=>{
                            if(res.data.success===false){
                            setSubs({errors:res.data.message})
                            }
                            else{
                                console.log('gagaga')
                                setSubs({subscriptions:res.data.subs})
                                console.log("hfhf",subs.subscriptions)
                     
                        if(subs.subscriptions){
                        console.log("helooo")

                        subs.subscriptions.forEach(element => {
                        console.log(element.OfferId)

                            axios.get(`http://localhost:5000/api/getOfferById?offerid=${element.OfferId}`)
                            .then(res=>{
                               // subList.push(res.data.offers)
                               if(res.data.success===false){
                                setSubs({errors:res.data.message})
                                }
                                else{
                                    subs.subList.push(res.data.offers)
                                    console.log(subs.subList)
                                  

                                }

                            })

                        });
                         setSubs({loading:true})
                         console.log(subs.loading)
                    }}
             })  }   
             })
            }
        })
        }
    }

    fetchMyAPI()
       
       }, [subs.errors,subs.loading])


    return (
        <>
        <UserNavBar />
        <h1 className="userTitle" style={{color:"#555"}}>Welcome to your space <br/>{!subs.userr ?  null : subs.userr.firstName} { !subs.userr ?  null : subs.userr.lastName} !</h1>
        <div className="UserSpace">
            <h2 >Your subscriptions</h2>
            <div className="cards" style={{top:"40px"}}>
            {subs.errors ? <h2>{subs.errors}</h2> : 

            
                !subs.loading ? <h2>Loading ...</h2> : 
                    
                subs.subList.map((offer, i) =>{
                        return (
                            <h1>hahaa</h1>
                        /*<div className="card card-0" style={{width:"80%", height:"100px"}}>
                        <div class="card__icon"> ggg</div>
                        <h3 class="card__title">{offer.internet}+ {offer.calls}</h3>
    </div>*/
                        )}
                        )
                        }

            </div>
        </div>
        </>
    )
}

export default SubscriptionPage
