          if(res.data.success==='true'){
            this.setState({token:token})
          }
           
        } );
  }}


  const handleChange = e => {
    e.preventDefault();
      const { name, value } = e.target
      setUser({
          ...user,
          [name]: value
      });
      
  }

  const register = (event) => {
    event.preventDefault();
      const { firstName,lastName, email, password, password2 } = user
