import React, { Component,useState } from 'react';
import './Register.css';
import {Link} from 'react-router-dom';
import axios from "axios"
import { useHistory } from "react-router-dom";
const localStorageObjectName = "login_system_storage";
import {getFromStorage,setInStorage} from '../utils/storage';


const  Login =()=> {
  const history = useHistory()
  const [ user, setUser] = useState({
      email:"",
      password:"",
      token:''
  })

  const handleChange = e => {
    e.preventDefault();
      const { name, value } = e.target
      setUser({
          ...user,
          [name]: value
      });
      
  }
  const componentDidMount= ()=> {
    // get the localstorage object
    const obj = getFromStorage(localStorageObjectName);
    if (obj && obj.token) {
      // get token from local storage
      const { token } = obj;

      // verify token
      axios.get(`/api/verify?token=${token}`)
        .then(res =>{
          if(res.data.success===true){
          
            this.setState({token:token})
            console.log('tok==',token)
          }
           
        } );
  }}

  const login = (event) => {
    event.preventDefault();
      const { email, password } = user
      console.log("ooooo",user);
      if( email && password ){
        console.log("logiin");
          axios.post("http://localhost:5000/api/login", user)
          .then(res => {
            

            if(res.data.success===true){
              if(user.email==="amal@gmail.com"){
                history.push('./AdminPage');
              }else{
              history.push('./HomePage');
            }}
           
     
        })
          console.log(' logged in');
      } else {
          alert("invlid input")
      }
      
  }
   
  
  
    return (
        <form className="form">
      <Link to="/" className="btn-flat">
        <i className="material-icons left">keyboard_backspace</i> Back to
        home
      </Link>
        <ul className="tab-group">
          <li className="tab"><a href="/register">Sign Up</a></li>
          <li className="tab active"><a>Log In</a></li>
        </ul>
        
        <div className="tab-content">
            <h1>Welcome Back!</h1>
            
            <form >
            
              <div className="field-wrap" noValidate >
              <input type="email" 
               placeholder="Adresse Email"
               required autoComplete="on"
               value={user.email}
               onChange={handleChange }
               name="email"
                            
               />


            </div>
            
            <div className="field-wrap" noValidate >
              <input type="password" 
              placeholder="Mot de passe" 
              required autoComplete="off"
              name="password"
              value={user.password}
              onChange={handleChange }
             />

            </div>
            
            <p className="forgot"><a href="#">Forgot Password?</a></p>
            
            <button className="button button-block"   onClick={login}>Log In</button>
            <p className="have-account"><a href="#">Vous n'avez pas de compte? Créez un!</a></p>
            </form>
  
          </div>
        
  </form> 
    )
}


export default Login
