import React,{Component} from 'react';
import './AdminPage.css';
import AdminNavBar from '../../layout/AdminNavbar';
import UserDescription from '../../layout/UserDescription';
import axios from "axios";



class AdminPage extends Component {
    state = {
        loaded: false,
        data:{},
        length:0
      };
    
      componentDidMount() {
        console.log('componentDidMount() lifecycle');
        axios.get("http://localhost:5000/api/getAllUsers")
        .then(res => {
            if(res.data.success===true){
            this.setState({ loaded: true,data:res.data.data,length:res.data.length });
            }else {
            this.setState({ loaded: false,data:"Error in loading data" });
            }
        })


        // Trigger update
      }
    
      render() {

    return (
        <>
        <AdminNavBar />

        <div className="HomePageAdmin">
            <h1 className="titleADmin">
            ADMIN PANEL
            </h1>

            <div className="grid-container">
                <div className="grid-item" id='item1'>Users</div>
                <div className="grid-item" id='item2'>Offers</div>
                <div className="grid-item" id='item3' >Invoices</div>
                <div className="grid-item" id='item4'>subscription</div>
           
            </div>
        </div>



        
        
        <div className='UsersSection'>
            <div className="UsersConainer">
                <h1 className='UsersSectionTitle'>USERS' LIST</h1>
                { !this.state.loaded ? <p>Error</p> :

                
                <table id="customers">
                <tr>
                    <th>ID</th>
                    <th>EMAIL</th>
                    <th>FIRST NAME</th>
                    <th>LAST NAME</th>
                   
                </tr>
                        
                        {    this.state.data.map((user, i) =>
                             <UserDescription                         
                                id={ user._id}
                                email={ user.email}
                                firstname={ user.firstName}
                                lastname={ user.lastName}
                
                            />)
                        }
                
                </table>
               
                }

                    

            </div>
        </div>

        <div className="offers">
            <h1 className="offersTitle">Offers</h1>
           
    
  
  <section class="input-content">
    <div class="input-content-wrap">
      <dl class="inputbox">
        <dt class="inputbox-title">Input Text</dt>
        <dd class="inputbox-content">
          <input id="input0" type="text" required/>
          <label for="input0">ID</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <dl class="inputbox">
        <dt class="inputbox-title">Input Password</dt>
        <dd class="inputbox-content">
          <input id="input1" type="password" required/>
          <label for="input1">Password</label>
          <span class="underline"></span>
        </dd>
      </dl>
      <div class="btns">
          <button class="btn btn-confirm">Sign In</button>
          <button class="btn btn-cancel">Cancel</button>
      </div>
    </div>
  </section>
</div>


       
        </>
    )
}
}

export default AdminPage
